import React, { useContext } from 'react';
import GameContext from './context/GameContext';
import GameBoard from './GameBoard';

function TicTacToe() {
  const { gameState, resetGame } = useContext(GameContext);

  const victoryArchivedInLine = (gameBoard) => {
    for (let i = 0; i <= 6; i += 3) {
      if (
        gameBoard[i] === gameBoard[i + 1]
        && gameBoard[i + 1] === gameBoard[i + 2]
        && gameBoard[i] !== 0
      ) return gameBoard[i];
    }
    return false;
  }

  const victoryArchivedInColumn = (gameBoard) => {
    for (let i = 0; i <= 2; i += 1) {
      if (
        gameBoard[i] === gameBoard[i + 3]
        && gameBoard[i + 3] === gameBoard[i + 6]
        && gameBoard[i] !== 0
      ) return gameBoard[i];
    }
    return false;
  }

  const victoryArchivedInDiagonals = (gameBoard) => {
    if (gameBoard[4] === 0) return false;
    if (gameBoard[0] === gameBoard[4] && gameBoard[4] === gameBoard[8]) {
      return gameBoard[0];
    }
    if (gameBoard[2] === gameBoard[4] && gameBoard[4] === gameBoard[6]) {
      return gameBoard[2];
    }
    return false;
  }

  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     activePlayer: 1,
  //     gameBoard: [0, 0, 0, 0, 0, 0, 0, 0, 0],
  //   };

  //   this.updateState = this.updateState.bind(this);
  //   this.resetGame = this.resetGame.bind(this);
  //   this.renderButton = this.renderButton.bind(this);
  // }

  // resetGame() {
  //   this.setState({
  //     activePlayer: 1,
  //     gameBoard: [0, 0, 0, 0, 0, 0, 0, 0, 0],
  //   });
  // }

  // toggleActivePlayer() {
  //   const { activePlayer } = this.state;
  //   if (activePlayer === 1) return 2;
  //   return 1;
  // }

  // updateState(cellClicked) {
  //   this.setState((state) => {
  //     const newState = [...state.gameBoard];
  //     let newActivePlayer = state.activePlayer;

  //     if (state.gameBoard[cellClicked] === 0) {
  //       newState[cellClicked] = state.activePlayer;
  //       newActivePlayer = this.toggleActivePlayer();
  //     } else newState[cellClicked] = state.gameBoard[cellClicked];

  //     return {
  //       activePlayer: newActivePlayer,
  //       gameBoard: newState,
  //     };
  //   });
  // }

  const victoryArchieved = (gameBoard) => {
    return (
      victoryArchivedInLine(gameBoard)
      || victoryArchivedInColumn(gameBoard)
      || victoryArchivedInDiagonals(gameBoard)
    );
  }

  const renderButton = () => {
    return (
      <button
        type="button"
        onClick={resetGame}
        data-testid="restart-button"
      >
        Recome√ßar Jogo
      </button>
    );
  }

  const win = victoryArchieved(gameState.gameBoard);
  if (!gameState.gameBoard.includes(0) && !win) {
    return (
      <>
        {renderButton()}
        <h1>Empate</h1>
      </>
    );
  }
  return (
    <>
      {renderButton()}
      {(!win)
        ? (
          <GameBoard
            gameState={gameBoard}
            updateGame={this.updateState}
          />
        )
        : <h1>{`Player ${win === 2 ? 'O' : 'X'} Ganhou`}</h1>}
    </>
  );
}

export default TicTacToe;
